  describe "GET index" do
    it "assigns all slideshows as @slideshows" do
      Slideshow.stub(:all) { [mock_slideshow] }
      get :index
      assigns(:slideshows).should eq([mock_slideshow])
    end
  end

  describe "GET show" do
    it "assigns the requested slideshow as @slideshow" do
      Slideshow.stub(:find).with("37") { mock_slideshow }
      get :show, :id => "37"
      assigns(:slideshow).should be(mock_slideshow)
