  end

  describe "GET new" do
    it "assigns a new slideshow as @slideshow" do
      Slideshow.stub(:new) { mock_slideshow }
      get :new
      assigns(:slideshow).should be(mock_slideshow)
    end
  end

  describe "GET edit" do
    it "assigns the requested slideshow as @slideshow" do
      Slideshow.stub(:find).with("37") { mock_slideshow }
      get :edit, :id => "37"
      assigns(:slideshow).should be(mock_slideshow)
    end
  end

  describe "POST create" do
    describe "with valid params" do
      it "assigns a newly created slideshow as @slideshow" do
        Slideshow.stub(:new).with({'these' => 'params'}) { mock_slideshow(:save => true) }
        post :create, :slideshow => {'these' => 'params'}
        assigns(:slideshow).should be(mock_slideshow)
      end

      it "redirects to the created slideshow" do
        Slideshow.stub(:new) { mock_slideshow(:save => true) }
        post :create, :slideshow => {}
        response.should redirect_to(slideshow_url(mock_slideshow))
      end
    end

    describe "with invalid params" do
      it "assigns a newly created but unsaved slideshow as @slideshow" do
        Slideshow.stub(:new).with({'these' => 'params'}) { mock_slideshow(:save => false) }
        post :create, :slideshow => {'these' => 'params'}
        assigns(:slideshow).should be(mock_slideshow)
      end

      it "re-renders the 'new' template" do
        Slideshow.stub(:new) { mock_slideshow(:save => false) }
        post :create, :slideshow => {}
        response.should render_template("new")
      end
    end
  end

  describe "PUT update" do
    describe "with valid params" do
      it "updates the requested slideshow" do
        Slideshow.stub(:find).with("37") { mock_slideshow }
        mock_slideshow.should_receive(:update_attributes).with({'these' => 'params'})
        put :update, :id => "37", :slideshow => {'these' => 'params'}
      end

      it "assigns the requested slideshow as @slideshow" do
        Slideshow.stub(:find) { mock_slideshow(:update_attributes => true) }
        put :update, :id => "1"
