        response.should redirect_to(slideshow_url(mock_slideshow))
      end
    end

    describe "with invalid params" do
      it "assigns the slideshow as @slideshow" do
        Slideshow.stub(:find) { mock_slideshow(:update_attributes => false) }
        put :update, :id => "1"
        assigns(:slideshow).should be(mock_slideshow)
      end

      it "re-renders the 'edit' template" do
        Slideshow.stub(:find) { mock_slideshow(:update_attributes => false) }
        put :update, :id => "1"
        response.should render_template("edit")
      end
    end
  end

  describe "DELETE destroy" do
